def makecharbuf(chardict:dict) -> list:
    charlen=len(chardict['0'])
    b,blankchar=[charlen],[]
    for i in range(0,charlen):
        blankchar.append(0)
    for i in range(0,256):
        ch=chr(i)
        if ch in chardict: c=chardict[chr(i)]
        else: c=blankchar
        b+=c
    return b

