def itergetcharfromfontbuf(charbuf:list,character:str) -> int:
    start=ord(character)*charbuf[0]+1
    end=start+charbuf[0]
    for i in range(start,end): yield charbuf[i]

def getcharfont(charbuf:list,character:str) -> list: 
    return [b for b in  itergetcharfromfontbuf(charbuf,character)]
    
